const ADD_DATA_BAG = 'ADD_DATA_BAG'
const UPDATE_TODAY_TEXTAREA = 'UPDATE_TODAY_TEXTAREA'

const todayReduser = (state, action) => {

    switch (action.type) {

        case ADD_DATA_BAG:
            let temp = {
                number: 8,
                error: "8888888888",
                textError: this._state.today.textarea
            }
            state.push(temp);
            state.textarea = '';
            return state;
        case UPDATE_TODAY_TEXTAREA:
            state.textarea = action.ubdate_Textarea;
            return state;
    }
}
export default todayReduser;
