const ADD_DATA_BAG = 'ADD_DATA_BAG'
const UPDATE_TODAY_TEXTAREA = 'UPDATE_TODAY_TEXTAREA'

let store = {
    _state: {
        Ord_Shoop: [
            { path: "Buy_subscription", name: "Заказ" },
            { path: "test2", name: "БД" },
            { path: "kk", name: "КК" },
            { path: "ord", name: "Оформление" },
            { path: "kontakt", name: "Контакты" },
            { path: "time_work", name: "Время работы" },
            { path: "books", name: "Буквы" }
        ],
        today: {
            data_today: [
                { path: "order", name: "Заказы" },
                { path: "tehno", name: "Технишн" },
                { path: "bag", name: "Баги" },
                { path: "aktion", name: "Акции" },
                { path: "info", name: "Инфо" }
            ],
            data_bag: [
                { number: 1, error: "Ошибка 10", textError: "Ошибка  <br />ппц" },
                { number: 2, error: "Ошибка 1ddd0", textError: "dddddddddddddddddd ппц" },
                { number: 3, error: "Ошибка 1fff0", textError: "Ошибка ппц" },
                { number: 4, error: "Ошибка fff10", textError: "Ошибка dsdsdsdsdsdsdsssssssss   <br /> <br /> <br />dddddddddddddddddddddd ппц" },
                { number: 5, error: "Ошибка 1ddd0", textError: "Ошибка  <br />ппц" },
                { number: 53232323, error: "edd0", textError: "Ошибка eeeeeeeeeeeeeeeeeeeeeeппц" },
            ],
            textarea: "DDDDD DDDDD"
        }

    },
    _render_Tree() { },
    getState() { return this._state },
    subscribe(observer) { this._render_Tree = observer; },



    dispatch(action) {
        if (action.type === ADD_DATA_BAG) {
            let temp = {
                number: 8,
                error: "8888888888",
                textError: this._state.today.textarea
            }
            this._state.today.data_bag.push(temp);
            this._state.today.textarea = '';
            this._render_Tree(this._state);
        }
        else if (action.type === UPDATE_TODAY_TEXTAREA) {
            this._state.today.textarea = action.ubdate_Textarea;
            this._render_Tree(this._state);
        }
    }

}

export const add_Date_Bag_ActioaCreator =()=>{
    return{
        type:ADD_DATA_BAG
    }
}
export const change_Bag_ActioaCreator=(text_value)=>{
    return{
        type:UPDATE_TODAY_TEXTAREA,
        ubdate_Textarea:text_value
    }
}




export default store;
