let store = {
    _state: {
        Ord_Shoop: [
            { path: "Buy_subscription", name: "Заказ" },
            { path: "test2", name: "БД" },
            { path: "kk", name: "КК" },
            { path: "ord", name: "Оформление" },
            { path: "kontakt", name: "Контакты" },
            { path: "time_work", name: "Время работы" },
            { path: "books", name: "Буквы" }
        ],
        today: {
            data_today: [
                { path: "order", name: "Заказы" },
                { path: "tehno", name: "Технишн" },
                { path: "bag", name: "Баги" },
                { path: "aktion", name: "Акции" },
                { path: "info", name: "Инфо" }
            ],
            data_bag: [
                { number: 1, error: "Ошибка 10", textError: "Ошибка  <br />ппц" },
                { number: 2, error: "Ошибка 1ddd0", textError: "dddddddddddddddddd ппц" },
                { number: 3, error: "Ошибка 1fff0", textError: "Ошибка ппц" },
                { number: 4, error: "Ошибка fff10", textError: "Ошибка dsdsdsdsdsdsdsssssssss   <br /> <br /> <br />dddddddddddddddddddddd ппц" },
                { number: 5, error: "Ошибка 1ddd0", textError: "Ошибка  <br />ппц" },
                { number: 53232323, error: "edd0", textError: "Ошибка eeeeeeeeeeeeeeeeeeeeeeппц" },
            ],
            textarea: "ad3333333333bag"
        }

    },
    render_Tree() {

    },
    addData_bag() {
        let temp = {
            number: 8,
            error: "8888888888",
            textError: state.today.textarea
        }
        state.today.data_bag.push(temp);
        state.today.textarea = '';
        render_Tree(state);
    },
    ubdateTodayTextarea(ubdate_Textarea) {
        state.today.textarea = ubdate_Textarea;
        render_Tree(state);
    },
    subscribe(observer) {
        render_Tree = observer;
    }
}






export default store;
